vst_vue_components.items.logo=Vue.component('logo',{props:['title'],template:"#template_logo_pm",computed:{staticPath(){return window.guiStaticPath||"/static/";},},});vst_vue_components.widgets.w_history_chart=Vue.component('w_history_chart',{mixins:[w_line_chart_mixin],data(){return{with_content_header:true,};},components:{content_header:{mixins:[base_widget_mixin],template:"#template_w_history_chart_content_header",data(){return{period_options:[{value:1095,title:'last 3 years'},{value:365,title:'last year'},{value:90,title:'last 3 months'},{value:30,title:'last month'},{value:14,title:'last 2 weeks'},{value:7,title:'last week'},{value:3,title:'last 3 days'},],};},methods:{isOptionSelected(option){return option.value==this.item.period.query_amount;},onChangeHandler(value){this.item.setChartPeriod(value);}},},content_body:{mixins:[w_line_chart_content_body_mixin],template:"#template_w_history_chart_content_body",data(){return{chart_instance:undefined,customizer:guiCustomizer,};},watch:{'customizer.skin.name':function(value){this.updateChartData();}},computed:{progressBarsData(){return this.item.getProgressBarsData(this.value);}},methods:{getProgressBarStyles(stats){let width=stats.sum/stats.all*100;let line=this.item.lines[stats.status.toLowerCase()];let bgc=this.item._getChartLineColor(line);return'width: {0}%; background-color: {1}!important;'.format([width,bgc]);}},},},});